import _ from 'lodash';
import { NextApiRequest, NextApiResponse } from 'next';
import { webtask, DEALS_TASK } from 'hooks/webtask';
import { encodeHTML } from 'hooks/render';

export default async (req:NextApiRequest, res:NextApiResponse) => {
    try {
        const deals = await webtask({
            taskName: DEALS_TASK,
            taskPath: 'US/today',
            body: {},
            cache: true
        });
        const articles = deals.map((deal: Object) => {
            const payload = _.get(deal, 'payload', {});
            const dealUrl = `https://freelook-now.herokuapp.com/ecom/it/${_.get(payload, 'asin', '')}?deal=${_.get(payload, 'promoDiscount', '')}`;
            const item = `<item>
                <title>Deal: ${_.get(payload, 'asin', '')}</title>
                <link>${dealUrl}</link>
                <guid isPermaLink="false">${_.get(payload, 'asin', '')}</guid>
                <pubDate>${(new Date()).toString().split("+")[0]}</pubDate>
                <description><a href="${dealUrl}" target="_blank">${encodeHTML(_.get(payload, 'promoText', _.get(payload, 'promoDescription', '')))}</a></description>
                <source url="${dealUrl}">amzn.to</source>
            </item>`;
            return item;
        });
        const feed = `<?xml version="1.0" encoding="UTF-8" standalone="yes"?>
            <rss version="2.0" xmlns:media="http://search.yahoo.com/mrss/">
            <channel>
                <generator>NFE/5.0</generator>
                <title>${'Best Deals'}</title>
                <link>${'https://freelook-now.herokuapp.com/ecom/deals'}</link>
                <language>en-US</language>
                <description>${'Best Deals in United States'}</description>
                ${articles.join('\n')}
            </channel>
            </rss>`;
        res.send(feed);
    } catch(e) {
        res.status(500).end();
    }
}


